function GenerarTabla(n){var i=$("#example").DataTable();i.destroy();listado="";let t=new Date;t=t.getDate();n.forEach(n=>{listado+=`<tr><td>
                ${n.Descripcion}
                </td>
                <td>
                ${n.Codigo}
                </td>
                <td>
                <a class="me-3" href="/Articulo/DetalleArticulo/ver/${n.IdArticulo}" data-toggle="tooltip"
                    title="Ver"><i class="fas fa-eye"></i></a>
                <a class="me-3" href="/Articulo/DetalleArticulo/editar/${n.IdArticulo}" data-toggle="tooltip"
                    title="Editar"><i class="fas fa-edit"></i></a>`,listado+=n.BorradoPor==null?`
            <a href="/articulo/eliminarArticulo/${n.IdArticulo}" data-toggle="tooltip" title="Borrar">
                <i class="fas fa-trash-alt"></i>
            </a>`:`<i title="Eliminado" class="fas fa-ban"></i>`,listado+=`</td></tr>`});$("#tbody").html(listado)}function ObtenerResultados(){let n={Descripcion:document.getElementById("descripcion").value,Codigo:document.getElementById("codigo").value,Eliminado:document.getElementById("eliminado").checked};fetch("https://localhost:44344/articulo/ObtenerFiltros",{method:"POST",headers:{"Content-type":"application/json"},body:JSON.stringify(n)}).then(n=>n.json()).then(n=>{GenerarTabla(n),Paginacion()})}function Paginacion(){$("#example").DataTable({ordering:!1,retrieve:!0,destroy:!0,language:{paginate:{first:"Primera",last:"Ultima",next:"Siguiente",previous:"Anterior"},lengthMenu:"Mostrar _MENU_ resultados por página",zeroRecords:"No se encontraron resultados",info:"Mostrando página  _PAGE_ de _PAGES_",infoEmpty:"Sin resultados",infoFiltered:"(filtered from _MAX_ total records)"}});$("#example_filter").hide()}const btnEliminar=document.getElementById("eliminado"),inpNombre=document.getElementById("descripcion"),inpEmail=document.getElementById("codigo");$(document).ready(function(){$(".select2Articulo").select2()});$(document).ready(function(){Paginacion();ObtenerResultados()});