function GenerarTabla(n){var t=$("#example").DataTable();t.destroy();listado="";n.forEach(n=>{listado+=`<tr><td>
                ${n.Cliente} - #${n.NroPedido}
                </td>
                <td>
                ${n.Estado}
                </td>
                <td>
                ${n.UltimaModificacion}
                </td>
                <td>
                <a class="me-3" href="/pedido/detallePedido/ver/${n.IdPedido}" data-toggle="tooltip"
                    title="Ver"><i class="fas fa-eye"></i></a>
                <a class="me-3" href="/pedido/detallePedido/editar/${n.IdPedido}" data-toggle="tooltip"
                    title="Editar"><i class="fas fa-edit"></i></a>`,listado+=n.seBorro?`<i title = "Eliminado" class="fas fa-ban" ></i > `:`
            <a onclick="eliminarPedido(${n.IdPedido})" data-toggle="tooltip" title = "Borrar" >
                <i class="fas fa-trash-alt"></i>
            </a > `,listado+=`</td ></tr >`});$("#tbody").html(listado)}function ObtenerResultados(){let n={Cliente:document.getElementById("cliente").value,Estado:document.getElementById("estado").value,Eliminado:document.getElementById("eliminado").checked,UltimosDosMeses:document.getElementById("ultimosDosMeses").checked};fetch("https://localhost:44344/pedido/ObtenerFiltros",{method:"POST",headers:{"Content-type":"application/json"},body:JSON.stringify(n)}).then(n=>n.json()).then(n=>{GenerarTabla(n),Paginacion()})}function Paginacion(){$("#example").DataTable({ordering:!1,retrieve:!0,destroy:!0,language:{paginate:{first:"Primera",last:"Ultima",next:"Siguiente",previous:"Anterior"},lengthMenu:"Mostrar _MENU_ resultados por página",zeroRecords:"No se encontraron resultados",info:"Mostrando página  _PAGE_ de _PAGES_",infoEmpty:"Sin resultados",infoFiltered:"(filtered from _MAX_ total records)"}});$("#example_filter").hide()}function eliminarPedido(n){swal({title:"¿Desea eliminar?",text:"¿Confirma que desea eliminar?",icon:"warning",buttons:["Cancelar","Aceptar"],dangerMode:!0}).then(t=>{t&&(window.location.href=`/Pedido/EliminarPedido/${n}`)})}const btnEliminar=document.getElementById("eliminado"),cliente=document.getElementById("cliente"),estado=document.getElementById("estado"),ultimosDosMeses=document.getElementById("ultimosDosMeses");$(document).ready(function(){$(".select2Pedido").select2()});$(document).ready(function(){Paginacion();ObtenerResultados()});